groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
projectDir = groovyUtils.projectPath

// Checking if method will be tested or not (information from project config file)
if(testRunner.testCase.testSuite.project.getPropertyValue(testRunner.testCase.getPropertyValue("MethodName"))=='1'){
	// Loading properties from file
	Properties props = new Properties()
	File propsFile = new File(projectDir+testRunner.testCase.getPropertyValue("PropertiesFilePath"))
	props.load(propsFile.newDataInputStream())
	props.each {
	testRunner.testCase.setPropertyValue(it.key,it.value)
	}
}
else{
	testRunner.cancel("Method was not marked for tests")
}

//take response from DB
groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
responseAsXml = groovyUtils.getXmlHolder('JDBC Request - DB QUERY#ResponseAsXml')
responseAsString = context.expand('${JDBC Request - DB QUERY#ResponseAsXml}')

//take random CountryName from DB response
random = new Random()
randomCountryName = random.nextInt(responseAsString.count('</Row>'))+1

CountryName = responseAsXml.getNodeValue('//Row['+randomCountryName+']//COUNTRY_NAME')

//save CountryName in properties
testRunner.testCase.setPropertyValue("CountryName", CountryName)

//save test case properties in external properties file
groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
projectDir = groovyUtils.projectPath
path = projectDir+testRunner.testCase.getPropertyValue("PropertiesFilePath")

String[] file = new File(path)
FileWriter fw = new FileWriter(path, true);
BufferedWriter bw = new BufferedWriter(fw);
PrintWriter out = new PrintWriter(bw)

file_new = file

CountryName = "CountryName="

//overwrite properties in external file
for (i=0; i<file.size(); i++) {
 file_new[i] = file[i]
  if (file[i].contains(CountryName)) {
  file_new[i] =  CountryName + testRunner.testCase.getPropertyValue("CountryName")
 }
}

FileOutputStream fot = new FileOutputStream(path);

for (i=0; i<file_new.size(); i++) {
 out.println(file_new[i])
}
out.close()

import java.io.File
groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
projectDir = groovyUtils.projectPath

//create path for method output folder
folderPath = projectDir+testRunner.testCase.getPropertyValue("ReqResPath") + File.separator + "webservicex"

//take request and response from SOAP step in method test case
Request =  context.expand('${GetCitiesByCountry#Request}')
Response = context.expand('${GetCitiesByCountry#Response}')

//save request and response in external xml file in environment subfolder (output)
def requestFile = new File(folderPath, "request_" + testRunner.testCase.getPropertyValue("MethodName") + ".xml")
requestFile.write(Request, "UTF-8")
def responseFile = new File(folderPath, "response_" + testRunner.testCase.getPropertyValue("MethodName") + ".xml")
responseFile.write(Response, "UTF-8")
